import React, { useRef,useEffect } from 'react';
const ClubsSection = () => {
  const sectionRef = useRef(null);
  const cardRefs = useRef([]);
  
  const Clubs = [
    {
      title: "Mathematics club",
      image : "data:image/jpeg;base64,",
      color: "violet-600"
    },
    {
      title: "CSED",
      image : "data:image/jpeg;base64,",
      color: "blue-800"
    },
    {
      title: "Microsoft Innovation Club",
      image : "data:image/jpeg;base64,",
      color: "cyan-400"
    },
    {
      title: "Codechef",
      image : "data:image/jpeg;base64,",
      color: "green-700"
    },
    {
      title: "Hackclub",
      image : "data:image/jpeg;base64,",
      color: "lime-400"
    },
    {
      title: "Cloud-Ops",
      image : "data:image/jpeg;base64,",
      color: "yellow-700"
    },
    {
      title: "Voice-IT",
      image : "data:image/jpeg;base64,",
      color : "orange-500"
    },
    {
      title: "Dramatics Club",
      image : "data:image/png;base64,",
      color: "red-600"
    },
    {
      title: "ACM-W",
      image : "data:image/png;base64,",
      color: "pink-500"
    }
  ];

 useEffect(() => {
    const loadGSAP = async () => {
      if (!window.gsap) {
        await new Promise((resolve) => {
          const script = document.createElement('script');
          script.src = 'https://cdnjs.cloudflare.com/ajax/libs/gsap/3.12.2/gsap.min.js';
          script.onload = resolve;
          document.head.appendChild(script);
        });
      }

      if (!window.ScrollTrigger) {
        await new Promise((resolve) => {
          const script = document.createElement('script');
          script.src = 'https://cdnjs.cloudflare.com/ajax/libs/gsap/3.12.2/ScrollTrigger.min.js';
          script.onload = resolve;
          document.head.appendChild(script);
        });
      }

      window.gsap.registerPlugin(window.ScrollTrigger);
      const startX = -400;
      const startY = window.innerHeight + 200;
      
      cardRefs.current.forEach((card, index) => {
        if (card) {
          if (index === 0) {
            window.gsap.set(card, { 
              x: 0, 
              y: 0,
              rotation: 0,
              scale: 1,
              opacity: 1,
              visibility: "visible"
            });
          } else {
            window.gsap.set(card, { 
              x: startX, 
              y: startY,
              rotation: -45,
              scale: 0.3,
              opacity: 0,
              visibility: "hidden"
            });
          }
        }
      });
      const tl = window.gsap.timeline({
        scrollTrigger: {
          trigger: sectionRef.current,
          start: "top top",
          end: "+=400%", 
          scrub: 1,
          pin: true,
          anticipatePin: 1,
          id: "clubCards"
        }
      });
      const centerX = 0;
      const centerY = 0;
      cardRefs.current.forEach((card, index) => {
        if (card && index > 0) {
          const stackOffset = index * 3;
          const rotationVariation = (index % 2 === 0 ? 1 : -1) * (index * 2); 
          
          tl.to(card, {
            x: centerX + (index % 2 === 0 ? stackOffset : -stackOffset),
            y: centerY - (index * 5),
            rotation: 360 + rotationVariation,
            scale: 1,
            opacity: 1,
            visibility: "visible",
            duration: 3,
            ease: "power2.out"
          }, (index - 1) * 1.2); 
        }
      });
    };

    loadGSAP();

    return () => {
      if (window.ScrollTrigger) {
        window.ScrollTrigger.getAll().forEach(trigger => trigger.kill());
      }
    };
  }, []);

  return (
    <section ref={sectionRef} className="min-h-screen py-20 px-4 overflow-hidden">
      <div className="max-w-7xl mx-auto text-center">
        <div className="mb-16">
          <h2 className="text-5xl font-bold text-white mb-6">
            College<span className="text-transparent bg-clip-text bg-gradient-to-r from-purple-400 to-purple-600">Clubs</span>
          </h2>
        </div>
        <div className="relative h-96 flex items-center justify-center">
          {Clubs.map((club, index) => (
            <div 
              key={index}
              ref={el => cardRefs.current[index] = el}
              className={`absolute w-72 h-80 bg-gradient-to-br from-${club.color} to-${club.color.replace('600', '800')} rounded-2xl overflow-hidden shadow-2xl border border-${club.color.replace('600', '500')}/30 cursor-pointer hover:scale-105 transition-transform duration-300`}
            >
              <div className="relative h-full">
                <img 
                  src={club.image} 
                  alt={club.title}
                  className="w-full h-2/3 object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-transparent to-transparent"></div>
                <div className="absolute bottom-0 left-0 right-0 p-4">
                  <h3 className="text-white font-bold text-lg text-center">
                    {club.title}
                  </h3>
                </div>
              </div>
            </div>
          ))}
        </div>

      </div>
    </section>
  );
};

export default ClubsSection;